mapper.select:
SELECT TRANSACTION_ID, OPERATION_ID, ACCOUNTING_DATE, VALUE_DATE, TYPE_ENUMERATION, TYPE_VALUE, AMOUNT, CURRENCY, DESCRIPTION
FROM TRANSACTION
WHERE TRANSACTION_ID = #{transactionId, jdbcType=VARCHAR}

mapper.insert:
INSERT INTO TRANSACTION (
    TRANSACTION_ID
,     OPERATION_ID
,     ACCOUNTING_DATE
,     VALUE_DATE
,     TYPE_ENUMERATION
,     TYPE_VALUE
,     AMOUNT
,     CURRENCY
,     DESCRIPTION
) VALUES (
        #{transactionId, jdbcType=VARCHAR}
    ,     #{operationId, jdbcType=VARCHAR}
    ,     #{accountingDate, jdbcType=DATE}
    ,     #{valueDate, jdbcType=DATE}
    ,     #{typeEnumeration, jdbcType=VARCHAR}
    ,     #{typeValue, jdbcType=VARCHAR}
    ,     #{amount, jdbcType=NUMERIC}
    ,     #{currency, jdbcType=VARCHAR}
    ,     #{description, jdbcType=VARCHAR}
    )
